import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom';
import { parseLocalJwt, splitUrl } from '../commons/Utils'
import '../../App.css'

function NavBar() {
    const [page, setPage] = useState("");
    const [count, setCount] = useState(0);
    const [url, setUrl] = useState(splitUrl(window.location.pathname)[0]);
    const [logged, setLogged] = useState(false);

    useEffect(() => {
        console.log('componentWillUpdate- runs on every update');
        setUrl(splitUrl(window.location.pathname)[0]);


        if (localStorage.getItem('token') != null) {
            setLogged(true);
        } else {
            setLogged(false);
        }

        console.log("URL " + url);
        console.log("isLogged" + logged)

    }, [url])

    function logoutUser() {
        localStorage.clear();
    }

    return (
        <nav className="w-52 h-screen bg-blue-100">
            <div className=" ">
                <div className="">
                    <button type="button" className="navbar-toggle" data-toggle="collapse" data-target="#myNavbar" style={{ border: '2px solid white' }}>
                        <span className="icon-bar"></span>
                        <span className="icon-bar"></span>
                        <span className="icon-bar"></span>
                    </button>
                    <a className="text-3xl border-l-gray-700" href="/" >codeflex</a>
                </div>
                {logged ?
                    <div>
                        {/* USER IS LOGGED IN */}
                        <div className="" id="myNavbar">
                            <ul className="">

                            </ul>
                            <ul className="">
                                <li className={url === 'practise' ? 'active-page' : ''}><Link to="/practise"><p>Practise</p></Link></li>
                                <li className={url === 'compete' ? 'active-page' : ''}><Link to="/compete"><p>Compete</p></Link></li>
                                <li className={url === 'leaderboard' ? 'active-page' : ''}><Link to="/leaderboard"><p>Leaderboard</p></Link></li>
                                <li className="">
                                    <Link to="/" className="" data-toggle="dropdown">
                                        <p>
                                            {parseLocalJwt().username}&nbsp;
                                            <span class="caret"></span>
                                        </p>
                                    </Link>

                                    <ul className="">
                                        <li><Link to={"/user/" + parseLocalJwt().username}><p>Profile</p></Link></li>
                                        {parseLocalJwt().role === "CONTENT_MANAGER" ?
                                            <li><Link to="/manage"><p>Manage Content</p></Link></li> : ''
                                        }
                                        <li><Link to="/" onClick={logoutUser}><p>Logout</p></Link></li>
                                    </ul>
                                </li>
                            </ul>
                        </div>
                    </div>

                    :
                    <div>
                        {/* USER IS LOGGED OUT */}
                        <div className="" id="myNavbar">
                            <ul className="">

                            </ul>
                            <ul className="">
                                <li><Link to="/login"><p>Login</p></Link></li>
                            </ul>
                        </div>
                    </div >
                }
            </div>
        </nav>
    );


}

export default NavBar
