import React, { useState, useEffect } from 'react'
import { splitUrl } from '../commons/Utils'

function NavBar() {
    const [page, setPage] = useState("");
    const [count, setCount] = useState(0);
    const [url, setUrl] = useState(splitUrl(window.location.pathname)[0]);
    const [logged, setLogged] = useState(false);

    useEffect(() => {
        console.log('componentWillUpdate- runs on every update');
        setUrl(splitUrl(window.location.pathname)[0]);


        if (localStorage.getItem('token') != null) {
            setLogged(true);
        } else {
            setLogged(false);
        }

        console.log("URL " + url);
        console.log("isLogged" + logged)

    }, [url])




    return (
        <nav className="navbar navbar-inverse">
            <div className="container">
                <div className="navbar-header">
                    <button type="button" className="navbar-toggle" data-toggle="collapse" data-target="#myNavbar" style={{ border: '2px solid white' }}>
                        <span className="icon-bar"></span>
                        <span className="icon-bar"></span>
                        <span className="icon-bar"></span>
                    </button>
                    <a className="navbar-brand" href="/" style={{ fontFamily: 'Roboto Condensed, sans-serif' }}>codeflex</a>
                </div>
                {logged ? <div>Logged</div> :
                    <div>
                        <div className="collapse navbar-collapse" id="myNavbar">
                            <ul className="nav navbar-nav">

                            </ul>
                            <ul className="nav navbar-nav navbar-right">
                                <li><Link to="/login"><p>Login</p></Link></li>
                            </ul>
                        </div>
                    </div >
                }
            </div>
        </nav>
    );


}

export default NavBar
